import React, { useRef, useState, useEffect } from "react";
import perspective from "perspective-transform";

function App() {
  const canvasRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [drawOffset, setDrawOffset] = useState({ x: 0, y: 0 });
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [cropPoints, setCropPoints] = useState([]);
  const [textInputs, setTextInputs] = useState([]);
  const [currentText, setCurrentText] = useState("");
  const [isAddingText, setIsAddingText] = useState(false);
  const [history, setHistory] = useState([]);
  const [redoHistory, setRedoHistory] = useState([]);
  const [brightness, setBrightness] = useState(100);
  const [contrast, setContrast] = useState(100);
  const [fontSize, setFontSize] = useState(20);
  const [fontColor, setFontColor] = useState("#000000");
  const [fontBold, setFontBold] = useState(false);
  const [fontItalic, setFontItalic] = useState(false);

  const CANVAS_WIDTH = 800;
  const CANVAS_HEIGHT = 600;

  const saveToHistory = () => {
    const canvas = canvasRef.current;
    const data = canvas.toDataURL();
    setHistory((prev) => [...prev, data]);
    setRedoHistory([]);
  };

  const undo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);
      setImageSrc(canvas.toDataURL());
    };
    const lastState = history[history.length - 1];
    setRedoHistory((prev) => [...prev, canvas.toDataURL()]);
    img.src = lastState;
    setHistory((prev) => prev.slice(0, -1));
  };

  const redo = () => {
    if (redoHistory.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);
      setImageSrc(canvas.toDataURL());
    };
    const lastRedo = redoHistory[redoHistory.length - 1];
    setHistory((prev) => [...prev, canvas.toDataURL()]);
    img.src = lastRedo;
    setRedoHistory((prev) => prev.slice(0, -1));
  };

  const applyAdjustments = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;
    const brightnessFactor = brightness / 100;
    const contrastFactor = contrast / 100;

    for (let i = 0; i < data.length; i += 4) {
      data[i] = contrastFactor * (data[i] - 128) + 128 * brightnessFactor;
      data[i + 1] = contrastFactor * (data[i + 1] - 128) + 128 * brightnessFactor;
      data[i + 2] = contrastFactor * (data[i + 2] - 128) + 128 * brightnessFactor;
    }

    ctx.putImageData(imageData, 0, 0);
    saveToHistory();
  };

  const handleAddText = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const style = `${fontBold ? "bold" : "normal"} ${fontItalic ? "italic" : "normal"} ${fontSize}px Arial`;
    const newText = { text: currentText, x, y, style, color: fontColor };
    setTextInputs([...textInputs, newText]);
    setIsAddingText(false);
    setCurrentText("");
    saveToHistory();
  };

  useEffect(() => {
    if (!imageSrc) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, drawOffset.x, drawOffset.y, imageSize.width, imageSize.height);
      textInputs.forEach(({ text, x, y, style, color }) => {
        ctx.font = style || `${fontSize}px Arial`;
        ctx.fillStyle = color || "black";
        ctx.fillText(text, x, y);
      });
    };
    img.src = imageSrc;
  }, [imageSrc, textInputs]);

  return (
    <div className="flex h-screen">
      <aside className="w-72 bg-gray-100 dark:bg-gray-800 p-4 overflow-y-auto">
        <h1 className="text-xl font-bold mb-4">🖌️ Editify Pro</h1>

        <input type="file" accept="image/*" onChange={(e) => {
          const file = e.target.files[0];
          if (!file) return;
          const reader = new FileReader();
          reader.onload = (event) => {
            const img = new Image();
            img.onload = () => {
              const canvas = canvasRef.current;
              const ctx = canvas.getContext("2d");
              const widthRatio = CANVAS_WIDTH / img.width;
              const heightRatio = CANVAS_HEIGHT / img.height;
              const scale = Math.min(widthRatio, heightRatio);
              const newWidth = img.width * scale;
              const newHeight = img.height * scale;
              const offsetX = (CANVAS_WIDTH - newWidth) / 2;
              const offsetY = (CANVAS_HEIGHT - newHeight) / 2;
              canvas.width = CANVAS_WIDTH;
              canvas.height = CANVAS_HEIGHT;
              ctx.clearRect(0, 0, canvas.width, canvas.height);
              ctx.drawImage(img, offsetX, offsetY, newWidth, newHeight);
              setImageSrc(event.target.result);
              setImageSize({ width: newWidth, height: newHeight });
              setDrawOffset({ x: offsetX, y: offsetY });
              saveToHistory();
            };
            img.src = event.target.result;
          };
          reader.readAsDataURL(file);
        }} className="mb-4" />

        <button onClick={undo} className="w-full py-2 px-4 bg-gray-500 text-white rounded mb-2">Undo</button>
        <button onClick={redo} className="w-full py-2 px-4 bg-green-500 text-white rounded mb-2">Redo</button>

        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Brightness</label>
          <input type="range" min="0" max="200" value={brightness} onChange={(e) => setBrightness(+e.target.value)} className="w-full" />
          <label className="block text-sm font-medium mb-1 mt-2">Contrast</label>
          <input type="range" min="0" max="200" value={contrast} onChange={(e) => setContrast(+e.target.value)} className="w-full" />
          <button onClick={applyAdjustments} className="mt-2 w-full py-2 px-4 bg-blue-600 text-white rounded">Apply</button>
        </div>

        <div className="mb-4">
          <label className="block text-sm mb-1">Text</label>
          <input type="text" value={currentText} onChange={(e) => setCurrentText(e.target.value)} className="w-full p-2 border rounded mb-2" placeholder="Enter text" />
          <label className="block text-sm mb-1">Font Size</label>
          <input type="number" value={fontSize} onChange={(e) => setFontSize(+e.target.value)} className="w-full p-2 border rounded mb-2" />
          <label className="block text-sm mb-1">Font Color</label>
          <input type="color" value={fontColor} onChange={(e) => setFontColor(e.target.value)} className="w-full mb-2" />
          <label className="block text-sm font-medium mb-1">Style</label>
          <div className="flex items-center gap-2 mb-2">
            <label><input type="checkbox" checked={fontBold} onChange={() => setFontBold(!fontBold)} /> Bold</label>
            <label><input type="checkbox" checked={fontItalic} onChange={() => setFontItalic(!fontItalic)} /> Italic</label>
          </div>
          <button onClick={() => setIsAddingText(true)} className="w-full py-2 px-4 bg-yellow-500 text-white rounded">Add Text</button>
        </div>
      </aside>

      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          onClick={isAddingText ? handleAddText : undefined}
          className="shadow-lg border border-gray-300"
        />
      </main>
    </div>
  );
}

export default App;
