import React, { useState } from 'react';
import { Sun, Moon } from 'lucide-react';

export default function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [image, setImage] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className={`${darkMode ? 'dark' : ''}`}>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white transition-colors">
        <header className="flex items-center justify-between p-4 border-b dark:border-gray-700">
          <div className="text-2xl font-bold">üñåÔ∏è Editify</div>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
          >
            {darkMode ? <Sun /> : <Moon />}
          </button>
        </header>

        <div className="flex h-[90vh]">
          <aside className="w-48 bg-white dark:bg-gray-800 border-r dark:border-gray-700 p-4 space-y-4">
            <label className="w-full block">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
              <span className="cursor-pointer w-full block px-4 py-2 bg-blue-500 text-white rounded text-center">
                Upload
              </span>
            </label>
            <button className="w-full px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded">Crop</button>
            <button className="w-full px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded">Resize</button>
            <button className="w-full px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded">Filters</button>
            <button className="w-full px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded">Text</button>
          </aside>

          <main className="flex-1 flex items-center justify-center bg-gray-50 dark:bg-gray-800">
            {image ? (
              <img src={image} alt="Uploaded" className="max-h-[80%] max-w-[90%] rounded shadow-lg" />
            ) : (
              <div className="border-4 border-dashed border-gray-300 w-[70%] h-[80%] flex items-center justify-center text-gray-400">
                Upload an image to start editing
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
}
