import React, { useRef, useState, useEffect } from "react";
import perspective from "perspective-transform";

function App() {
  const canvasRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [drawOffset, setDrawOffset] = useState({ x: 0, y: 0 });
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [cropPoints, setCropPoints] = useState([]);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [textInputs, setTextInputs] = useState([]);
  const [currentText, setCurrentText] = useState("");
  const [isAddingText, setIsAddingText] = useState(false);
  const [history, setHistory] = useState([]);
  const [redoHistory, setRedoHistory] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const [isDrawingMode, setIsDrawingMode] = useState(false);
  const [brushColor, setBrushColor] = useState("#000000");
  const [brushSize, setBrushSize] = useState(5);

  const CANVAS_WIDTH = 800;
  const CANVAS_HEIGHT = 600;

  const saveToHistory = () => {
    const canvas = canvasRef.current;
    const data = canvas.toDataURL();
    setHistory((prev) => [...prev, data]);
    setRedoHistory([]);
  };

  const undo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    const last = history[history.length - 1];
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);
      setImageSrc(canvas.toDataURL());
    };
    img.src = last;
    setRedoHistory((prev) => [...prev, canvas.toDataURL()]);
    setHistory((prev) => prev.slice(0, -1));
  };

  const redo = () => {
    if (redoHistory.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    const last = redoHistory[redoHistory.length - 1];
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);
      setImageSrc(canvas.toDataURL());
    };
    img.src = last;
    setHistory((prev) => [...prev, canvas.toDataURL()]);
    setRedoHistory((prev) => prev.slice(0, -1));
  };

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const widthRatio = CANVAS_WIDTH / img.width;
        const heightRatio = CANVAS_HEIGHT / img.height;
        const scale = Math.min(widthRatio, heightRatio);

        const newWidth = img.width * scale;
        const newHeight = img.height * scale;
        const offsetX = (CANVAS_WIDTH - newWidth) / 2;
        const offsetY = (CANVAS_HEIGHT - newHeight) / 2;

        canvas.width = CANVAS_WIDTH;
        canvas.height = CANVAS_HEIGHT;

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, offsetX, offsetY, newWidth, newHeight);

        setImageSrc(event.target.result);
        setImageSize({ width: newWidth, height: newHeight });
        setDrawOffset({ x: offsetX, y: offsetY });
        setDimensions({ width: newWidth, height: newHeight });

        setCropPoints([
          { x: offsetX + 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + newHeight - 20 },
          { x: offsetX + 20, y: offsetY + newHeight - 20 },
        ]);
        saveToHistory();
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
  };

  const handleMouseDown = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    if (isAddingText) {
      setTextInputs([...textInputs, { text: currentText, x, y }]);
      setIsAddingText(false);
      setCurrentText("");
      saveToHistory();
      return;
    }

    if (isCropping) {
      cropPoints.forEach((point, idx) => {
        const dx = x - point.x;
        const dy = y - point.y;
        if (Math.sqrt(dx * dx + dy * dy) < 10) {
          setDraggingPoint(idx);
        }
      });
    }

    if (isDrawingMode) {
      setIsDrawing(true);
      const ctx = canvasRef.current.getContext("2d");
      ctx.strokeStyle = brushColor;
      ctx.lineWidth = brushSize;
      ctx.beginPath();
      ctx.moveTo(x, y);
    }
  };

  const handleMouseMove = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    if (isCropping && draggingPoint !== null) {
      const updated = [...cropPoints];
      updated[draggingPoint] = { x, y };
      setCropPoints(updated);
    }

    if (isDrawingMode && isDrawing) {
      const ctx = canvasRef.current.getContext("2d");
      ctx.lineTo(x, y);
      ctx.stroke();
    }
  };

  const handleMouseUp = () => {
    if (isDrawingMode && isDrawing) {
      const ctx = canvasRef.current.getContext("2d");
      ctx.closePath();
      setIsDrawing(false);
      saveToHistory();
    }
    setDraggingPoint(null);
  };

  return (
    <div className="flex">
      <aside className="w-64 p-4 bg-gray-100 dark:bg-gray-800">
        <h1 className="text-xl font-bold mb-4">üñåÔ∏è Editify</h1>

        <input type="file" accept="image/*" onChange={handleUpload} className="mb-4" />

        <button onClick={undo} className="w-full bg-gray-500 text-white py-2 rounded mb-2">Undo</button>
        <button onClick={redo} className="w-full bg-gray-500 text-white py-2 rounded mb-2">Redo</button>

        <button onClick={() => setIsDrawingMode(!isDrawingMode)} className={`w-full py-2 rounded mb-2 ${isDrawingMode ? "bg-red-600 text-white" : "bg-blue-600 text-white"}`}>
          {isDrawingMode ? "Disable Draw" : "Enable Draw"}
        </button>

        <label className="block mb-2">Brush Color</label>
        <input type="color" value={brushColor} onChange={(e) => setBrushColor(e.target.value)} className="mb-4" />

        <label className="block mb-2">Brush Size</label>
        <input type="range" min="1" max="50" value={brushSize} onChange={(e) => setBrushSize(Number(e.target.value))} className="mb-4 w-full" />
      </aside>

      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          className="border border-gray-300 shadow-lg"
        />
      </main>
    </div>
  );
}

export default App;
