import React, { useRef, useState, useEffect } from "react";

function App() {
  const canvasRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [drawOffset, setDrawOffset] = useState({ x: 0, y: 0 });
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [cropPoints, setCropPoints] = useState([]);

  const CANVAS_WIDTH = 800;
  const CANVAS_HEIGHT = 600;

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        canvas.width = CANVAS_WIDTH;
        canvas.height = CANVAS_HEIGHT;

        // Fit image inside canvas with aspect ratio
        const widthRatio = CANVAS_WIDTH / img.width;
        const heightRatio = CANVAS_HEIGHT / img.height;
        const scale = Math.min(widthRatio, heightRatio);

        const newWidth = img.width * scale;
        const newHeight = img.height * scale;
        const offsetX = (CANVAS_WIDTH - newWidth) / 2;
        const offsetY = (CANVAS_HEIGHT - newHeight) / 2;

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, offsetX, offsetY, newWidth, newHeight);

        setImageSrc(event.target.result);
        setImageSize({ width: newWidth, height: newHeight });
        setDrawOffset({ x: offsetX, y: offsetY });
        setDimensions({ width: newWidth, height: newHeight });

        // Crop corners inside visible bounds
        setCropPoints([
          { x: offsetX + 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + newHeight - 20 },
          { x: offsetX + 20, y: offsetY + newHeight - 20 },
        ]);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
  };

  const redrawCanvas = () => {
    if (!imageSrc) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(
        img,
        drawOffset.x,
        drawOffset.y,
        imageSize.width,
        imageSize.height
      );

      if (isCropping && cropPoints.length === 4) {
        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(cropPoints[0].x, cropPoints[0].y);
        for (let i = 1; i < 4; i++) {
          ctx.lineTo(cropPoints[i].x, cropPoints[i].y);
        }
        ctx.closePath();
        ctx.stroke();

        cropPoints.forEach((pt) => {
          ctx.fillStyle = "blue";
          ctx.beginPath();
          ctx.arc(pt.x, pt.y, 6, 0, 2 * Math.PI);
          ctx.fill();
        });
      }
    };
    img.src = imageSrc;
  };

  useEffect(() => {
    redrawCanvas();
  }, [imageSrc, cropPoints, isCropping]);

  const handleMouseDown = (e) => {
    if (!isCropping) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    cropPoints.forEach((point, idx) => {
      const dx = x - point.x;
      const dy = y - point.y;
      if (Math.sqrt(dx * dx + dy * dy) < 10) {
        setDraggingPoint(idx);
      }
    });
  };

  const handleMouseMove = (e) => {
    if (!isCropping || draggingPoint === null) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const updated = [...cropPoints];
    updated[draggingPoint] = { x, y };
    setCropPoints(updated);
  };

  const handleMouseUp = () => {
    setDraggingPoint(null);
  };

  const handleResize = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      canvas.width = dimensions.width;
      canvas.height = dimensions.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, dimensions.width, dimensions.height);
    };
    img.src = imageSrc;
  };

  const handleDownload = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "edited-image.png";
    link.click();
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">🖌️ Editify</h1>

        <input
          type="file"
          accept="image/*"
          onChange={handleUpload}
          className="mb-4"
        />

        <button
          className="w-full py-2 px-4 bg-blue-500 text-white rounded mb-2"
          onClick={() => setShowResize(!showResize)}
        >
          Resize
        </button>

        {showResize && (
          <div className="space-y-2 mb-4">
            <input
              type="number"
              placeholder="Width"
              value={dimensions.width}
              onChange={(e) =>
                setDimensions({ ...dimensions, width: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Height"
              value={dimensions.height}
              onChange={(e) =>
                setDimensions({ ...dimensions, height: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <button
              onClick={handleResize}
              className="w-full py-2 px-4 bg-green-600 text-white rounded"
            >
              Apply Resize
            </button>
          </div>
        )}

        <button
          className={`w-full py-2 px-4 rounded mb-2 ${
            isCropping ? "bg-red-500 text-white" : "bg-gray-200 dark:bg-gray-700"
          }`}
          onClick={() => setIsCropping(!isCropping)}
        >
          {isCropping ? "Exit Crop Mode" : "Crop"}
        </button>

        <button
          onClick={handleDownload}
          className="w-full py-2 px-4 bg-purple-600 text-white rounded"
        >
          Download
        </button>
      </aside>

      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          className="shadow-lg border border-gray-300"
        />
      </main>
    </div>
  );
}
import perspective from "perspective-transform";

function getPerspectiveTransform(src, dst) {
  return perspective(src, dst);
}

function getTransformedImage(img, transform, width, height) {
  const temp = document.createElement("canvas");
  temp.width = img.width;
  temp.height = img.height;
  const ctx = temp.getContext("2d");
  ctx.drawImage(img, 0, 0);
  const srcData = ctx.getImageData(0, 0, img.width, img.height);

  const dstCanvas = document.createElement("canvas");
  dstCanvas.width = width;
  dstCanvas.height = height;
  const dstCtx = dstCanvas.getContext("2d");
  const dstData = dstCtx.createImageData(width, height);

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      const [srcX, srcY] = transform.inverse([x, y]);
      const ix = Math.floor(srcX);
      const iy = Math.floor(srcY);

      if (ix >= 0 && iy >= 0 && ix < img.width && iy < img.height) {
        const srcIndex = (iy * img.width + ix) * 4;
        const dstIndex = (y * width + x) * 4;

        for (let i = 0; i < 4; i++) {
          dstData.data[dstIndex + i] = srcData.data[srcIndex + i];
        }
      }
    }
  }

  dstCtx.putImageData(dstData, 0, 0);
  return dstData;
}

export default App;
