import React, { useState, useRef } from "react";

function App() {
  const [imageSrc, setImageSrc] = useState(null);
  const [activeTool, setActiveTool] = useState(null);
  const [width, setWidth] = useState(300);
  const [height, setHeight] = useState(300);
  const canvasRef = useRef(null);

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (event) => setImageSrc(event.target.result);
    reader.readAsDataURL(file);
  };

  const handleResize = () => {
    if (!imageSrc) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      canvas.width = width;
      canvas.height = height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, width, height);
    };
    img.src = imageSrc;
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <aside className="w-64 p-4 border-r min-h-screen bg-white dark:bg-gray-900">
        <h1 className="text-2xl font-bold mb-6">ðŸŽ¨ Editify</h1>

        <input
          type="file"
          accept="image/*"
          onChange={handleUpload}
          className="mb-6"
        />

        <button
          onClick={() => setActiveTool("resize")}
          className="w-full px-4 py-2 mb-3 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Resize
        </button>
        <button
          onClick={() => setActiveTool("crop")}
          className="w-full px-4 py-2 mb-3 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Crop
        </button>
        <button
          onClick={() => setActiveTool("filters")}
          className="w-full px-4 py-2 mb-3 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Filters
        </button>
        <button
          onClick={() => setActiveTool("text")}
          className="w-full px-4 py-2 mb-3 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Text
        </button>

        {/* Resize tool options */}
        {activeTool === "resize" && (
          <div className="mt-6">
            <label className="block mb-2">Width:</label>
            <input
              type="number"
              value={width}
              onChange={(e) => setWidth(parseInt(e.target.value))}
              className="w-full px-2 py-1 border mb-4"
            />

            <label className="block mb-2">Height:</label>
            <input
              type="number"
              value={height}
              onChange={(e) => setHeight(parseInt(e.target.value))}
              className="w-full px-2 py-1 border mb-4"
            />

            <button
              onClick={handleResize}
              className="w-full px-4 py-2 bg-blue-500 text-white rounded"
            >
              Apply Resize
            </button>
          </div>
        )}
      </aside>

      {/* Main Canvas Area */}
      <main className="flex-1 flex items-center justify-center p-4">
        {imageSrc ? (
          <canvas ref={canvasRef} />
        ) : (
          <p className="text-gray-500">Upload an image to start editing</p>
        )}
      </main>
    </div>
  );
}

export default App;
