import React, { useRef, useState, useEffect } from "react";
import perspective from "perspective-transform";

function App() {
  const canvasRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [drawOffset, setDrawOffset] = useState({ x: 0, y: 0 });
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [cropPoints, setCropPoints] = useState([]);
  const [textInputs, setTextInputs] = useState([]);
  const [currentText, setCurrentText] = useState("");
  const [isAddingText, setIsAddingText] = useState(false);
  const [history, setHistory] = useState([]);

  const CANVAS_WIDTH = 800;
  const CANVAS_HEIGHT = 600;

  const saveToHistory = () => {
    const canvas = canvasRef.current;
    const data = canvas.toDataURL();
    setHistory((prev) => [...prev, data]);
  };

  const undo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);
      setImageSrc(canvas.toDataURL());
    };
    img.src = history[history.length - 1];
    setHistory((prev) => prev.slice(0, -1));
  };

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const widthRatio = CANVAS_WIDTH / img.width;
        const heightRatio = CANVAS_HEIGHT / img.height;
        const scale = Math.min(widthRatio, heightRatio);

        const newWidth = img.width * scale;
        const newHeight = img.height * scale;
        const offsetX = (CANVAS_WIDTH - newWidth) / 2;
        const offsetY = (CANVAS_HEIGHT - newHeight) / 2;

        canvas.width = CANVAS_WIDTH;
        canvas.height = CANVAS_HEIGHT;

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, offsetX, offsetY, newWidth, newHeight);

        setImageSrc(event.target.result);
        setImageSize({ width: newWidth, height: newHeight });
        setDrawOffset({ x: offsetX, y: offsetY });
        setDimensions({ width: newWidth, height: newHeight });

        setCropPoints([
          { x: offsetX + 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + newHeight - 20 },
          { x: offsetX + 20, y: offsetY + newHeight - 20 },
        ]);
        saveToHistory();
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
  };

  const redrawCanvas = () => {
    if (!imageSrc) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(
        img,
        drawOffset.x,
        drawOffset.y,
        imageSize.width,
        imageSize.height
      );

      textInputs.forEach(({ text, x, y }) => {
        ctx.font = "20px Arial";
        ctx.fillStyle = "black";
        ctx.fillText(text, x, y);
      });

      if (isCropping && cropPoints.length === 4) {
        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(cropPoints[0].x, cropPoints[0].y);
        for (let i = 1; i < 4; i++) {
          ctx.lineTo(cropPoints[i].x, cropPoints[i].y);
        }
        ctx.closePath();
        ctx.stroke();

        cropPoints.forEach((pt) => {
          ctx.fillStyle = "blue";
          ctx.beginPath();
          ctx.arc(pt.x, pt.y, 6, 0, 2 * Math.PI);
          ctx.fill();
        });
      }
    };
    img.src = imageSrc;
  };

  useEffect(() => {
    redrawCanvas();
  }, [imageSrc, cropPoints, isCropping, textInputs]);

  const handleMouseDown = (e) => {
    if (isAddingText) {
      const rect = canvasRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      setTextInputs([...textInputs, { text: currentText, x, y }]);
      setIsAddingText(false);
      setCurrentText("");
      saveToHistory();
      return;
    }
    if (!isCropping) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    cropPoints.forEach((point, idx) => {
      const dx = x - point.x;
      const dy = y - point.y;
      if (Math.sqrt(dx * dx + dy * dy) < 10) {
        setDraggingPoint(idx);
      }
    });
  };

  const handleMouseMove = (e) => {
    if (!isCropping || draggingPoint === null) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const updated = [...cropPoints];
    updated[draggingPoint] = { x, y };
    setCropPoints(updated);
  };

  const handleMouseUp = () => {
    setDraggingPoint(null);
  };

  const handleResize = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      canvas.width = dimensions.width;
      canvas.height = dimensions.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, dimensions.width, dimensions.height);
      saveToHistory();
    };
    img.src = imageSrc;
  };

  const handleCrop = () => {
    if (cropPoints.length !== 4) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      const srcCoords = cropPoints.map((pt) => [pt.x, pt.y]);
      const targetSize = 300;
      const dstCoords = [
        [0, 0],
        [targetSize, 0],
        [targetSize, targetSize],
        [0, targetSize],
      ];
      const transform = perspective(srcCoords, dstCoords);
      const output = document.createElement("canvas");
      output.width = targetSize;
      output.height = targetSize;
      const outCtx = output.getContext("2d");

      const imageData = outCtx.createImageData(targetSize, targetSize);
      for (let y = 0; y < targetSize; y++) {
        for (let x = 0; x < targetSize; x++) {
          const [sx, sy] = transform.inverse([x, y]);
          const px = Math.floor(sx);
          const py = Math.floor(sy);
          if (px >= 0 && py >= 0 && px < canvas.width && py < canvas.height) {
            const srcData = ctx.getImageData(px, py, 1, 1).data;
            const index = (y * targetSize + x) * 4;
            imageData.data[index] = srcData[0];
            imageData.data[index + 1] = srcData[1];
            imageData.data[index + 2] = srcData[2];
            imageData.data[index + 3] = srcData[3];
          }
        }
      }
      outCtx.putImageData(imageData, 0, 0);
      const newSrc = output.toDataURL();
      setImageSrc(newSrc);
      setIsCropping(false);
      setCropPoints([]);
      saveToHistory();
    };
    img.src = imageSrc;
  };

  const applyFilter = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    for (let i = 0; i < imageData.data.length; i += 4) {
      const avg = (imageData.data[i] + imageData.data[i + 1] + imageData.data[i + 2]) / 3;
      imageData.data[i] = avg;
      imageData.data[i + 1] = avg;
      imageData.data[i + 2] = avg;
    }
    ctx.putImageData(imageData, 0, 0);
    saveToHistory();
  };

  const handleDownload = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "edited-image.png";
    link.click();
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">🖌️ Editify</h1>

        <input type="file" accept="image/*" onChange={handleUpload} className="mb-4" />

        <button onClick={() => setShowResize(!showResize)} className="w-full py-2 px-4 bg-blue-500 text-white rounded mb-2">
          Resize
        </button>

        {showResize && (
          <div className="space-y-2 mb-4">
            <input type="number" placeholder="Width" value={dimensions.width} onChange={(e) => setDimensions({ ...dimensions, width: +e.target.value })} className="w-full p-2 border rounded" />
            <input type="number" placeholder="Height" value={dimensions.height} onChange={(e) => setDimensions({ ...dimensions, height: +e.target.value })} className="w-full p-2 border rounded" />
            <button onClick={handleResize} className="w-full py-2 px-4 bg-green-600 text-white rounded">
              Apply Resize
            </button>
          </div>
        )}

        <button onClick={applyFilter} className="w-full py-2 px-4 bg-indigo-600 text-white rounded mb-2">
          Grayscale Filter
        </button>

        <div className="mb-2">
          <input type="text" value={currentText} onChange={(e) => setCurrentText(e.target.value)} className="w-full p-2 border rounded mb-2" placeholder="Enter text" />
          <button onClick={() => setIsAddingText(true)} className="w-full py-2 px-4 bg-yellow-600 text-white rounded">
            Add Text
          </button>
        </div>

        <button onClick={undo} className="w-full py-2 px-4 bg-gray-500 text-white rounded mb-2">Undo</button>

        <button className={`w-full py-2 px-4 rounded mb-2 ${isCropping ? "bg-red-500 text-white" : "bg-gray-200 dark:bg-gray-700"}`} onClick={() => setIsCropping(!isCropping)}>
          {isCropping ? "Exit Crop Mode" : "Crop"}
        </button>

        {isCropping && (
          <button onClick={handleCrop} className="w-full py-2 px-4 bg-yellow-500 text-white rounded mb-2">
            Apply Crop
          </button>
        )}

        <button onClick={handleDownload} className="w-full py-2 px-4 bg-purple-600 text-white rounded">
          Download
        </button>
      </aside>

      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas ref={canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} onMouseDown={handleMouseDown} onMouseMove={handleMouseMove} onMouseUp={handleMouseUp} className="shadow-lg border border-gray-300" />
      </main>
    </div>
  );
}

export default App;
