import React, { useRef, useState } from "react";

function App() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0);
        setDimensions({ width: img.width, height: img.height });
      };
      img.src = event.target.result;
      setImage(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleResize = () => {
    if (!image || !dimensions.width || !dimensions.height) return;

    const img = new Image();
    img.onload = () => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      canvas.width = dimensions.width;
      canvas.height = dimensions.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, dimensions.width, dimensions.height);
    };
    img.src = image;
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">üñåÔ∏è Editify</h1>
        <input type="file" accept="image/*" onChange={handleUpload} className="mb-4" />

        {/* Resize tool toggle */}
        <button
          className="w-full py-2 px-4 bg-blue-500 text-white rounded mb-2"
          onClick={() => setShowResize(!showResize)}
        >
          Resize
        </button>

        {/* Resize UI */}
        {showResize && (
          <div className="space-y-2 mb-4">
            <input
              type="number"
              placeholder="Width"
              value={dimensions.width}
              onChange={(e) => setDimensions({ ...dimensions, width: +e.target.value })}
              className="w-full p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Height"
              value={dimensions.height}
              onChange={(e) => setDimensions({ ...dimensions, height: +e.target.value })}
              className="w-full p-2 border rounded"
            />
            <button
              onClick={handleResize}
              className="w-full py-2 px-4 bg-green-600 text-white rounded"
            >
              Apply Resize
            </button>
          </div>
        )}

        {/* Other tools (non-functional yet) */}
        <button className="w-full py-2 px-4 bg-gray-200 dark:bg-gray-700 rounded mb-2">Crop</button>
        <button className="w-full py-2 px-4 bg-gray-200 dark:bg-gray-700 rounded">Filters</button>
      </aside>

      {/* Canvas */}
      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas ref={canvasRef} className="shadow-lg border border-gray-300" />
      </main>
    </div>
  );
}

export default App;
