import React, { useRef, useState, useEffect } from "react";

function App() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [cropPoints, setCropPoints] = useState([]);

  // Upload image and draw it
 const handleUpload = (e) => {
  const file = e.target.files[0];
  if (!file) return;

  const reader = new FileReader();
  reader.onload = (event) => {
    const img = new Image();
    img.onload = () => {
      const maxWidth = 800;
      const maxHeight = 600;

      let newWidth = img.width;
      let newHeight = img.height;

      // Scale down if necessary
      if (img.width > maxWidth || img.height > maxHeight) {
        const widthRatio = maxWidth / img.width;
        const heightRatio = maxHeight / img.height;
        const scale = Math.min(widthRatio, heightRatio);

        newWidth = img.width * scale;
        newHeight = img.height * scale;
      }

      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");

      canvas.width = newWidth;
      canvas.height = newHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, newWidth, newHeight);

      setDimensions({ width: newWidth, height: newHeight });
      setCropPoints([
        { x: 20, y: 20 },
        { x: newWidth - 20, y: 20 },
        { x: newWidth - 20, y: newHeight - 20 },
        { x: 20, y: newHeight - 20 },
      ]);
    };
    img.src = event.target.result;
    setImage(event.target.result);
  };
  reader.readAsDataURL(file);
};


  // Redraw image + crop lines
  const redrawCanvas = () => {
    if (!image) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0);

      // Draw crop polygon
      if (isCropping && cropPoints.length === 4) {
        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(cropPoints[0].x, cropPoints[0].y);
        for (let i = 1; i < 4; i++) {
          ctx.lineTo(cropPoints[i].x, cropPoints[i].y);
        }
        ctx.closePath();
        ctx.stroke();

        // Draw drag points
        cropPoints.forEach((pt) => {
          ctx.fillStyle = "blue";
          ctx.beginPath();
          ctx.arc(pt.x, pt.y, 6, 0, 2 * Math.PI);
          ctx.fill();
        });
      }
    };
    img.src = image;
  };

  useEffect(() => {
    redrawCanvas();
  }, [image, isCropping, cropPoints]);

  // Handle mouse events
  const handleMouseDown = (e) => {
    if (!isCropping) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    cropPoints.forEach((point, idx) => {
      const dx = x - point.x;
      const dy = y - point.y;
      if (Math.sqrt(dx * dx + dy * dy) < 10) {
        setDraggingPoint(idx);
      }
    });
  };

  const handleMouseMove = (e) => {
    if (!isCropping || draggingPoint === null) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const updatedPoints = [...cropPoints];
    updatedPoints[draggingPoint] = { x, y };
    setCropPoints(updatedPoints);
  };

  const handleMouseUp = () => {
    setDraggingPoint(null);
  };

  const handleDownload = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "edited-image.png";
    link.click();
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">🖌️ Editify</h1>

        <input
          type="file"
          accept="image/*"
          onChange={handleUpload}
          className="mb-4"
        />

        {/* Resize */}
        <button
          className="w-full py-2 px-4 bg-blue-500 text-white rounded mb-2"
          onClick={() => setShowResize(!showResize)}
        >
          Resize
        </button>

        {showResize && (
          <div className="space-y-2 mb-4">
            <input
              type="number"
              placeholder="Width"
              value={dimensions.width}
              onChange={(e) =>
                setDimensions({ ...dimensions, width: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Height"
              value={dimensions.height}
              onChange={(e) =>
                setDimensions({ ...dimensions, height: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <button
              onClick={() => {
                const canvas = canvasRef.current;
                const ctx = canvas.getContext("2d");
                const img = new Image();
                img.onload = () => {
                  canvas.width = dimensions.width;
                  canvas.height = dimensions.height;
                  ctx.drawImage(
                    img,
                    0,
                    0,
                    dimensions.width,
                    dimensions.height
                  );
                };
                img.src = image;
              }}
              className="w-full py-2 px-4 bg-green-600 text-white rounded"
            >
              Apply Resize
            </button>
          </div>
        )}

        {/* Crop Tool */}
        <button
          className={`w-full py-2 px-4 rounded mb-2 ${
            isCropping ? "bg-red-500 text-white" : "bg-gray-200 dark:bg-gray-700"
          }`}
          onClick={() => setIsCropping(!isCropping)}
        >
          {isCropping ? "Exit Crop Mode" : "Crop"}
        </button>

        {/* Download */}
        <button
          onClick={handleDownload}
          className="w-full py-2 px-4 bg-purple-600 text-white rounded"
        >
          Download
        </button>
      </aside>

      {/* Canvas */}
      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas
          ref={canvasRef}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          className="shadow-lg border border-gray-300"
        />
      </main>
    </div>
  );
}

export default App;
