import React, { useRef, useState } from "react";

function App() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0);
      };
      img.src = event.target.result;
      setImage(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">üñåÔ∏è Editify</h1>
        <input type="file" accept="image/*" onChange={handleUpload} className="mb-4" />
        <button className="w-full py-2 px-4 bg-gray-200 dark:bg-gray-700 rounded mb-2">
          Resize
        </button>
        <button className="w-full py-2 px-4 bg-gray-200 dark:bg-gray-700 rounded mb-2">
          Crop
        </button>
        <button className="w-full py-2 px-4 bg-gray-200 dark:bg-gray-700 rounded">
          Filters
        </button>
      </aside>

      {/* Canvas */}
      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas ref={canvasRef} className="shadow-lg border border-gray-300" />
      </main>
    </div>
  );
}

export default App;
