import React, { useRef, useState, useEffect } from "react";

function App() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [showResize, setShowResize] = useState(false);
  const [dimensions, setDimensions] = useState({ width: "", height: "" });
  const [isCropping, setIsCropping] = useState(false);
  const [draggingPoint, setDraggingPoint] = useState(null);
  const [cropPoints, setCropPoints] = useState([]);

  // Upload image, auto-fit to canvas (800x600), center it
  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const MAX_WIDTH = 800;
        const MAX_HEIGHT = 600;

        canvas.width = MAX_WIDTH;
        canvas.height = MAX_HEIGHT;

        const widthRatio = MAX_WIDTH / img.width;
        const heightRatio = MAX_HEIGHT / img.height;
        const scale = Math.min(widthRatio, heightRatio);

        const newWidth = img.width * scale;
        const newHeight = img.height * scale;

        const offsetX = (MAX_WIDTH - newWidth) / 2;
        const offsetY = (MAX_HEIGHT - newHeight) / 2;

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, offsetX, offsetY, newWidth, newHeight);

        setImage(event.target.result);
        setDimensions({ width: newWidth, height: newHeight });
        setCropPoints([
          { x: offsetX + 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + 20 },
          { x: offsetX + newWidth - 20, y: offsetY + newHeight - 20 },
          { x: offsetX + 20, y: offsetY + newHeight - 20 },
        ]);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
  };

  // Redraw the canvas including crop overlay
  const redrawCanvas = () => {
    if (!image) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

      if (isCropping && cropPoints.length === 4) {
        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(cropPoints[0].x, cropPoints[0].y);
        for (let i = 1; i < 4; i++) {
          ctx.lineTo(cropPoints[i].x, cropPoints[i].y);
        }
        ctx.closePath();
        ctx.stroke();

        cropPoints.forEach((pt) => {
          ctx.fillStyle = "blue";
          ctx.beginPath();
          ctx.arc(pt.x, pt.y, 6, 0, 2 * Math.PI);
          ctx.fill();
        });
      }
    };
    img.src = image;
  };

  useEffect(() => {
    redrawCanvas();
  }, [image, isCropping, cropPoints]);

  // Handle crop point dragging
  const handleMouseDown = (e) => {
    if (!isCropping) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    cropPoints.forEach((point, idx) => {
      const dx = x - point.x;
      const dy = y - point.y;
      if (Math.sqrt(dx * dx + dy * dy) < 10) {
        setDraggingPoint(idx);
      }
    });
  };

  const handleMouseMove = (e) => {
    if (!isCropping || draggingPoint === null) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const updated = [...cropPoints];
    updated[draggingPoint] = { x, y };
    setCropPoints(updated);
  };

  const handleMouseUp = () => {
    setDraggingPoint(null);
  };

  // Resize tool
  const handleResize = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const img = new Image();
    img.onload = () => {
      canvas.width = dimensions.width;
      canvas.height = dimensions.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, dimensions.width, dimensions.height);
    };
    img.src = image;
  };

  // Download tool
  const handleDownload = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "edited-image.png";
    link.click();
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-100 dark:bg-gray-800 p-4">
        <h1 className="text-xl font-bold mb-4">üñåÔ∏è Editify</h1>

        <input
          type="file"
          accept="image/*"
          onChange={handleUpload}
          className="mb-4"
        />

        {/* Resize */}
        <button
          className="w-full py-2 px-4 bg-blue-500 text-white rounded mb-2"
          onClick={() => setShowResize(!showResize)}
        >
          Resize
        </button>

        {showResize && (
          <div className="space-y-2 mb-4">
            <input
              type="number"
              placeholder="Width"
              value={dimensions.width}
              onChange={(e) =>
                setDimensions({ ...dimensions, width: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Height"
              value={dimensions.height}
              onChange={(e) =>
                setDimensions({ ...dimensions, height: +e.target.value })
              }
              className="w-full p-2 border rounded"
            />
            <button
              onClick={handleResize}
              className="w-full py-2 px-4 bg-green-600 text-white rounded"
            >
              Apply Resize
            </button>
          </div>
        )}

        {/* Crop Toggle */}
        <button
          className={`w-full py-2 px-4 rounded mb-2 ${
            isCropping ? "bg-red-500 text-white" : "bg-gray-200 dark:bg-gray-700"
          }`}
          onClick={() => setIsCropping(!isCropping)}
        >
          {isCropping ? "Exit Crop Mode" : "Crop"}
        </button>

        {/* Download */}
        <button
          onClick={handleDownload}
          className="w-full py-2 px-4 bg-purple-600 text-white rounded"
        >
          Download
        </button>
      </aside>

      {/* Canvas Area */}
      <main className="flex-1 flex items-center justify-center bg-white dark:bg-gray-900">
        <canvas
          ref={canvasRef}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          className="shadow-lg border border-gray-300"
        />
      </main>
    </div>
  );
}

export default App;
